def bfs(root):
    to_visit=[root]
    senback=[root.val]
    while to_visit:
        current=to_visit.pop()
        if current.left:
            to_visit.append(current.left)
            senback.append(current.left.val)
        if current.right:
            to_visit.append(current.right)
            senback.append(current.right.val)
    return senback

